mapper.select:
select * from book_drift b1
where b1.book_id = #{bookId} and b1.drift_num =(
    select min(b2.drift_num) from book_drift b2 where b2.book_id = #{bookId}
);

select * from book_drift b1
where b1.book_id = #{bookId} and b1.drift_num =(
    select max(b2.drift_num) from book_drift b2 where b2.book_id = #{bookId}
);
